INSERT INTO `broadcast_text_locale` (`ID`, `locale`, `MaleText_lang`, `FemaleText_lang`, `VerifiedBuild`) VALUES
(99658, 'ruRU', 'Охотники на демонов, Азероту нужна ваша помощь.', '', 23420),
(99656, 'ruRU', 'Я верховный маг Кадгар, глава Кирин-Тора.', '', 23420),
(101616, 'ruRU', '', 'Они ведут себя, словно дети.$B$BНо я знала, что так и будет. Если повезет, они уладят свои разногласия прямо сейчас.', 23420),
(99552, 'ruRU', 'Ты убивал своих братьев и сестер! Ты наплевал на все наши жертвы!', '', 23420),
(99551, 'ruRU', 'Я верил только своим глазам, брат. Иллидан все более походил на наших врагов.', '', 23420),
(99550, 'ruRU', 'Иллидан научил нас сражаться и сдерживать Скверну. Вместе мы уничтожили орды демонов. А ТЫ выбрал путь предателя.', '', 23420),
(99549, 'ruRU', '', 'Довольно глупых пререканий. У нас мало времени!', 23420),
(104603, 'ruRU', 'Ваши союзники погибли. Мы сокрушим вас. Вам не видать солнечного света!', '', 23420),
(98916, 'ruRU', '', 'Многие выступали против Иллидана, но он был прав во всем. Мы – то оружие, что сокрушит Пылающий Легион.', 23420),
(103140, 'ruRU', '', 'Мы не можем потерять Кайна и Алтруиса. Их необходимо спасти!', 23420),
(99532, 'ruRU', '', 'Если не считать нас, то самые сильные узники были заточены на этом уровне. Ключевое слово – БЫЛИ.', 23420),
(99855, 'ruRU', '', 'Много лет Стражи держали нас в темных глубинах, вдали от всех. \n\nНо теперь наш мир ждет величайшее испытание в его истории. Мы должны защитить Азерот от его самого опасного врага.', 23420),
(104602, 'ruRU', 'Ваша настойчивость похвальна, но мои демоны захватили все уровни Казематов. Впереди вас ждет только смерть.', '', 23420),
(99852, 'ruRU', 'Своенравные зверюшки, если не знать, как держать их в узде. Но этот должен быть послушным... хотя бы какое-то время.', '', 23420),
(98783, 'ruRU', 'Кайн и Алтруис уже отправились наверх. Седлай сквернотопыря и догоняй их.', '', 23420),
(98807, 'ruRU', 'Не отступать. Никто не должен прорваться!', '', 23420),
(98806, 'ruRU', '', 'Держать строй, охотники на демонов. За владыку Иллидана!', 23420),
(101999, 'ruRU', 'Алтруис, ты не доверяешь никому и ни во что не веришь. Мне надоело с тобой спорить.', '', 23420),
(101995, 'ruRU', 'И ты ей поверил? Больше нас она ненавидит только Легион.', '', 23420),
(99530, 'ruRU', 'Вы слышали, что сказала Стражница. Только мы можем победить Легион. Мы ДОЛЖНЫ выжить.', '', 23420),
(102022, 'ruRU', 'Я сражался не меньше вашего! Но не убивал тысячи невинных.', '', 23420),
(99528, 'ruRU', 'Он сделал трудный выбор. Он пожертвовал ВСЕМ. А ты?', '', 23420),
(99527, 'ruRU', 'Ты шел за Иллиданом, словно пес, даже когда он сбился с пути.', '', 23420),
(99526, 'ruRU', 'Он сопротивлялся ей, как и все мы. Он нуждался в том, чтобы мы верили в него.', '', 23420),
(99525, 'ruRU', 'За десять тысяч лет заточения энергия Скверны подчинила Иллидана. Когда его освободили, он был не в своем уме.', '', 23420),
(99509, 'ruRU', 'О, мне хорошо знакомо это чувство.', '', 23420),
(99508, 'ruRU', '', 'Гул''дан не мог сам сюда пробраться. Кто-то из Стражей предал меня!', 23420),
(104600, 'ruRU', 'Ха-ха-ха... Неужели Стражи обратились за помощью к своим врагам? Сдавайся, иллидари! Если ты надеешься выбраться отсюда, тебе придется сразиться со МНОЙ.', '', 23420),
(104596, 'ruRU', 'Кайн всегда был верным солдатом, бесстрашным командиром и опытным стратегом.$B$BОднако мне кажется, что верность Иллидану ослепила его.$B$BПо мере того как росло его могущество, Иллидан стал терять контроль над энергией демонов, которую поглощал.$B$BЯ знал, что мои действия сочтут предательством, но я считал, что исполняю свой долг.$B$BА теперь по пути Иллидана пошел и Кайн. Иллидари не должны доверять свою судьбу такому лидеру, как он.', '', 23420),
(94864, 'ruRU', 'В какой-то момент я понял, что не могу следовать за Иллиданом. Он поддался демонической порче и стал превращаться в одного из своих заклятых врагов.', '', 23420),
(104592, 'ruRU', 'Много лет назад, когда владыка Иллидан был в Черном храме, Алтруис Страдалец ушел от нашего предводителя и охотников на демонов.$B$BРешив, что Иллидан стал тем самым чудовищем, которых ненавидел, Алтруис начал злоумышлять против нашего господина.$B$BОн погубил четырех наших товарищей – Аландиен, Тераса, Нетариеля и Варедиса.$B$BЭто были наши учителя, наши братья и сестры.$B$BЭтого я не могу ему простить.', '', 23420),
(94858, 'ruRU', 'Меня смущают мотивы Алтруиса, но пока что мы преследуем одну цель – выбраться из этой темницы.', '', 23420),
(91916, 'ruRU', 'Это было давно, Кайн. Я все еще верю в наше дело. Но уже никому слепо не подчиняюсь... даже Иллидану.', '', 23420),
(91600, 'ruRU', 'Почему Алтруис на свободе?! Он был врагом Иллидана!', '', 23420),
(99827, 'ruRU', '', 'Этот ключ доверил мне сам Саргерас. И вам его ни за что не получить!', 23420),
(99826, 'ruRU', '', 'Ты и твои охотники на демонов – ничто, $n!', 23420),
(99829, 'ruRU', '', 'Поторопись, Кайн уже вступил в бой с Тиранной.', 23420),
(100418, 'ruRU', '', 'Я дождусь остальных и займусь порталом в Черный храм.', 23420),
(99020, 'ruRU', '', 'Я поймала несколько сквернотопырей, $n. Используй их, когда решишь атаковать флагман Легиона.', 23420),
(99834, 'ruRU', 'Наверное, это к лучшему, что я остаюсь здесь, на Опорном пункте. Я еще не оправился от ран, которые получил во время своего заточения.\n\nЭтот мир переполняет демоническая энергия. Из-за нее я с трудом сдерживаю в себе энергию Скверны.', '', 23420),
(100733, 'ruRU', '', 'Для нас главное – заполучить саргеритовый ключ.\n\nПора зачистить склоны и отправляться наверх, на флагман Тиранны.', 23420),
(100737, 'ruRU', '', 'Не знаю, как ты, но я готова на все, чтобы выжить.\n\nНам нужны новые силы!', 23420),
(100749, 'ruRU', '', 'Если бы не нападение на Черный храм, я бы тут осталась подольше.\n\nЗдесь столько демонов!', 23420),
(100842, 'ruRU', '', 'Слуги мои, верните фолиант любой ценой!', 23420),
(99578, 'ruRU', '', 'Твоя душа пойдет в Реактор. Хоть какая-то польза.', 23420),
(99613, 'ruRU', '', 'Открой колодцы и выпусти в него поток душ.', 23420),
(99072, 'ruRU', '', 'Прошу тебя, сдержи обещание и помоги им.', 23420),
(99094, 'ruRU', '', '$n! Мои наги в ловушке властителя преисподней!', 23420),
(102029, 'ruRU', 'Гаардун?', '', 23420),
(99669, 'ruRU', '...военачальник?', '', 23420),
(99667, 'ruRU', '', 'Мы вступили в бой. Я помогу вам с надзирателем.', 23420),
(99666, 'ruRU', '', 'Мы у реактора душ, здесь очень много демонов.', 23420),
(99665, 'ruRU', 'Командиры?', '', 23420),
(99070, 'ruRU', '', '...', 23420),
(99069, 'ruRU', '', 'Скажи, ты не думала присоединиться к нам?', 23420),
(99068, 'ruRU', '', 'Забавно. Даже отсюда я чувствую твое смятение.', 23420),
(99067, 'ruRU', '', 'Это ты не доживешь, ведьма!', 23420),
(99066, 'ruRU', '', 'Я бы на тебя посмотрела. Жаль, до встречи ты не доживешь.', 23420),
(99065, 'ruRU', '', 'Ваше упорство похвально. Но знай – твои войска вот-вот обратятся в бегство.', 23420),
(103959, 'ruRU', '$n, мне кажется, в пещере находится нечто очень могущественное. Но поскольку весь Мардум насквозь пропитан энергией Скверны, я не могу утверждать наверняка. У меня все как в тумане.\n\nНо если там и правда что-то есть, мы обязаны выяснить, что это такое. Ты владеешь призрачным зрением лучше любого из нас, кроме разве что самого владыки Иллидана.\n\nМожет, проверишь пещеру с помощью своего "Призрачного зрения", чтобы подтвердить или опровергнуть мои подозрения?', '', 23420),
(98276, 'ruRU', '', 'Ты прав, нельзя подставлять врагу спину.', 23420),
(98273, 'ruRU', 'Мы выступим, только когда убедимся, что не попадем в засаду.', '', 23420),
(92162, 'ruRU', 'Я жду ваших указаний, $gмой господин:моя госпожа;.', '', 23420),
(96307, 'ruRU', '', 'Да? Чем я могу помочь тебе, $n?', 23420),
(95397, 'ruRU', '', 'Приветствую тебя, $n. Каков твой план?', 23420),
(94951, 'ruRU', '$n, теперь нам нужно разобраться с войсками Легиона на вулкане. Их следует уничтожить до того, как мы решим штурмовать "Молот Скверны", флагманский корабль королевы стаи.\n\nПотом мы покончим с ней, заберем саргеритовый ключ и вернемся в Черный храм, чтобы уничтожить захватчиков.\n\nНепростая задача, но, думаю, мы справимся.', '', 23420),
(98578, 'ruRU', '', 'Приказы будут? Починить что-нибудь перед началом похода?', 23420),
(101781, 'ruRU', '', 'Вы что, ослепли?! Почему эта тварь еще жива?', 23420),
(101780, 'ruRU', 'Мы едва прорвались. Большинство из нас... но и здесь обстрел не прекращается.', '', 23420),
(101779, 'ruRU', 'Поднимайся сюда, $n. Легион решил взяться за нас всерьез и выставил уничтожители.', '', 23420),
(101654, 'ruRU', '<Севис пожертвовал собой, чтобы открыть врата Легиона и доставить сюда отряд шиваррских демонесс.>\n\n<Его жертва не должна стать напрасной.>', '', 23420),
(103337, 'ruRU', 'Абсолютно. Я без колебаний отдам свою жизнь ради общей цели. Так поступил бы любой охотник на демонов.', '', 23420),
(101650, 'ruRU', 'Королева стаи что-то сделала с этими вратами. Нам не удалось их открыть даже с помощью нескольких душ мо''аргов.\n\nВозможно, нам следует принести в жертву душу охотника на демонов... Выбор за тобой, $n.\n\nКому-то из нас придется умереть.', '', 23420),
(99507, 'ruRU', 'Чтобы деактивировать шпили, выведи из строя их стабилизаторы.', '', 23420),
(101307, 'ruRU', 'Удачи тебе, $n. Встретимся на вулкане!', '', 23420),
(96689, 'ruRU', 'Белиаша защищают шпили страдания. Их нужно деактивировать.', '', 23420),
(103324, 'ruRU', 'Когда их предыдущий повелитель, Магтеридон, потерпел поражение, шиварры, как и многие другие демоны, присягнули на верность владыке Иллидану. Ему также служат наги, сломленные и иные создания.\n\nЕсли сам властитель Запределья решил принять их в услужение, то я не смею ставить под сомнение его выбор.', '', 23420),
(103080, 'ruRU', 'Горнило Пустоты? Это устройство позволяет следить за целью, а также общаться с собеседником на значительном расстоянии.\n\nС его помощью даже можно связываться с теми, кто находится в другом измерении.\n\nОно использует энергию Скверны. Чем дальше находится цель, тем больше энергии уходит на исполнение ритуала.', '', 23420),
(96144, 'ruRU', 'Рад тебя видеть, $n.\n\nА теперь давай перейдем к делу.', '', 23420),
(101647, 'ruRU', '<Сломленный-мистик еле дышит.>\n\nМеня уже не спасти. Делай то... что $gдолжен:должна;...', '', 23420),
(98847, 'ruRU', 'Инквизитор Зловеш? Кто это? Отвечай!', '', 23420),
(98846, 'ruRU', 'Охотники на демонов открывают врата!', '', 23420),
(100342, 'ruRU', '', 'Я допрашиваю одного демона за другим с помощью Косы Душ, но, похоже, они ничего не знают о местонахождении саргеритового ключа.\n\nОни лишь твердят о преданности некой королеве.\n\nЦиана и Джейс выбились вперед. Я боюсь, что Циана попалась в руки врагу, когда пыталась помочь другим нашим охотникам на демонов.\n\nУ меня очень нехорошее предчувствие...', 23420),
(98855, 'ruRU', 'Эти устройства заражают Скверной все вокруг себя.', '', 23420),
(98844, 'ruRU', 'Я требую полный отчет!', '', 23420),
(98843, 'ruRU', 'Что происходит? Кто активировал врата?', '', 23420),
(98960, 'ruRU', 'Давным-давно Саргерас сотворил Мардум, чтобы заточать в нем демонов. Он также создал особый саргеритовый ключ, с помощью которого запирал вход в этот мир.\n\nОднако, когда титан решил уничтожить все сущее, он расколол Мардум на мелкие осколки, которые разлетелись по всей Круговерти Пустоты. Так появился на свет Пылающий Легион.\n\nСаргерас спрятал свой ключ в этом фрагменте Мардума. С помощью ключа можно попасть в любой из миров Легиона. По плану владыки Иллидана именно этот ключ поможет нам уничтожить Легион.', '', 23420),
(100024, 'ruRU', 'Нужно уничтожить войска Легиона, которые представляют для нас угрозу в районе хребта, чтобы затем приступить к переброске остальных отрядов из Черного храма.', '', 23420),
(96305, 'ruRU', '', 'Убьем их всех!', 23420),
(103461, 'ruRU', 'Он позволит нам попасть в любой из миров Легиона, даже в Аргус.', '', 23420),
(100022, 'ruRU', 'Вы слышали приказ. Нужно найти ключ.', '', 23420),
(100571, 'ruRU', 'Кроме того, без него вам не выбраться отсюда. Найдите ключ и возвращайтесь в Черный храм.', '', 23420),
(100005, 'ruRU', 'Саргеритовый ключ – залог нашей победы над Пылающим Легионом.', '', 23420);
