INSERT INTO `quest_template_locale` (`ID`, `locale`, `LogTitle`, `LogDescription`, `QuestDescription`, `AreaDescription`, `PortraitGiverText`, `PortraitGiverName`, `PortraitTurnInText`, `PortraitTurnInName`, `QuestCompletionLog`, `VerifiedBuild`) VALUES
(39689, 'ruRU', 'Иллидари, пора в путь', 'Поговорите с верховным магом Кадгаром.', 'Легион начал наступление на наш мир. Храбрые защитники со всех его краев, от Калимдора до Восточных королевств, откликнулись на призыв к оружию.\n\nНо все они потерпят сокрушительное поражение, если к ним не присоединитесь вы. Именно вы, иллидари, – самое совершенное оружие против Легиона. \n\nЯ прошу вас следовать за мной. Уже скоро вы получите дальнейшие инструкции.', '', '', '', '', '', '', 23420),
(40255, 'ruRU', 'Между нами и свободой', 'Убейте Бастиллакса и заберите его силу.', 'Последние из выживших пробились в последний зал наверху.\n\nТеперь между нами и выходом из Казематов только одно препятствие – предводитель демонов Бастиллакс.\n\nУбей его, забери себе его силу и открой нам путь к свободе.', '', 'Этот демон охраняет выход из Казематов.', 'Бастиллакс', '', '', '', 23420),
(43248, 'ruRU', 'Штурм Сторожевой башни: Шпиль последователей Тьмы', '', '', '', '', '', '', '', '', 23420),
(40373, 'ruRU', 'Новое начало', 'Сделайте выбор между Кайном и Алтруисом.', '$n, как предводитель иллидари в отсутствие владыки Иллидана, ты $gдолжен:должна; сделать выбор: кто станет твоей правой рукой?\n\nКайн или Алтруис? Выбери одного из них.\n\nИзбранный тобой иллидари станет твоим личным защитником, и его взгляды повлияют на то, какой путь мы изберем в дальнейшем. Но кого бы ты ни $gвыбрал:выбрала;, наша главная цель останется неизменной.\n\nЕсли тебе нужно напоминание о прошлом, загляни в Источник Правосудия. Вспомни о жертвах, принесенных нами, чтобы стать охотниками на демонов. Скажи мне, когда будешь $gготов:готова; сделать выбор.', '', '', '', '', '', '', 23420),
(39686, 'ruRU', 'Путь наверх', 'Поднимитесь в Зал Правосудия.', 'Кор''вас отправилась вперед, чтобы догнать Кайна и Альтруиса.\n\nДождись, когда подъемник опустится, и поднимись в Зал Правосудия. Они ждут тебя.', '', '', '', '', '', '', 23420),
(38253, 'ruRU', 'Гарнизонная кампания: Военный совет', 'Поговорите с королем Варианом Ринном в ратуше вашего гарнизона.', 'Твой гарнизон гудит, как пчелиный улей. Сам король Вариан Ринн удостоил твою крепость своим визитом, чтобы подробно обсудить план вторжения в Танаанские джунгли.$b$bКороль со всей своей свитой в ратуше. Думаю, не стоит заставлять его ждать...', '', 'Король Ринн ожидает вас.', '', '', '', 'Поговорите с королем Варианом Ринном в ратуше вашего гарнизона.', 23420),
(40254, 'ruRU', 'Выковано в огне', 'Убейте Иммоланта и заберите его силу.', 'Грозный демон Иммолант возглавлял армию Легиона, которая покорила и поработила один далекий мир Нексуса.\n\nКаким-то образом Стражам удалось пленить его и заключить в камере, которая находится слева от нас. Не думаю, что стены камеры удержат его надолго.\n\nСила демона - ценный приз для любого охотника на демонов. Тот, кто победит Иммоланта, овладеет невероятным могуществом.\n\nХочешь попытаться, $n?\n', '', 'Иммолант полон чистейшей концентрированной энергии Скверны.', 'Иммолант', '', '', '', 23420),
(39684, 'ruRU', 'В лучах славы', 'Уничтожьте щит Смотрящего.', 'В восточной части блока расположена хитроумно сработанная тюремная камера. В ней держат созерцателя, известного как Смотрящий.$B$BК сожалению, ее механизм вышел из строя. $B$BТебе надо будет вручную повернуть зеркала в камере, чтобы направить луч энергии на это существо. Иначе мы не сможем пробить щит Смотрящего.\n\nТоропись. Алтруис отправился вперед, чтобы помочь остальным, но без тебя мы проиграем эту битву.', '', '', '', '', '', '', 23420),
(39685, 'ruRU', 'Застывший во времени', 'Активируйте системы безопасности в Каземате Льда.', 'Стражи держали здесь самых разных опасных монстров. Теперь же, когда войска Гул''дана прорвались в эти стены, присутствие этих монстров стало особенно... неудобным.\n\nНемалая часть наших сил сейчас брошена на то, чтобы удержать в южной камере жуткое существо – магмового разрушителя. Мы только вышли из стазиса и пока не в силах с ним справиться. Но можно попробовать снова включить системы безопасности камеры.\n\nКайн тоже отправился туда, но ему нужна твоя помощь. Вы должны любой ценой запечатать эту камеру!', '', '', '', '', '', '', 23420),
(39682, 'ruRU', 'Великий угонщик сквернотопырей', 'Оседлайте сквернотопыря и летите на верхний уровень Казематов.', '$gСлышал:Слышала;, что сказала Майев? Надо выбираться отсюда.\n\nВ дальнем конце пещер есть шахта подъемника. Туда мы и направимся.', '', '', '', '', '', '', 23420),
(40253, 'ruRU', 'Остановить Гул''дана!', 'Войдите в Каземат Предателя, убейте Крушителя и Молотителя и заберите их силу Скверны.', 'Победив Иллидана в Черном храме, мы забрали его безжизненное тело сюда, чтобы навечно похоронить в Казематах Стражей.\n\nОднако Гул''дану удалось проникнуть в Казематы. Он пытается похитить тело Предателя.\n\nДаже после смерти тело Иллидана может быть использовано в интересах Пылающего Легиона. Необходимо помешать Гул''дану!', '', 'Остановите его, если сможете.', 'Гул''дан', '', '', '', 23420),
(41257, 'ruRU', 'Штурм Сторожевой башни: вершина Корданы', '', '', '', '', '', '', '', '', 23420),
(38689, 'ruRU', 'Вливание Скверны', 'Поглотите 100 ед. энергии убитых демонов.', 'После стольких лет заключения мы очень слабы.\n\nКазематы кишат демонами. Убей их и собери их души, чтобы восполнить силы.', '', '', '', '', '', '', 23420),
(38690, 'ruRU', 'Возвращение иллидари', 'Освободите 8 заключенных иллидари.', 'Наши силы слишком неравны.\n\nНам понадобится больше союзников, если мы хотим выбраться отсюда целыми и невредимыми. Освободи иллидари, заточенных в камерах этого блока.\n\nВсе они помогут нам покинуть это место живыми.', '', '', '', '', '', '', 23420),
(7814, 'ruRU', 'Пожертвование – шелк', '', '', '', '', '', '', '', '', 23420),
(39742, 'ruRU', 'Казематы Стражей', '', '', '', '', '', '', '', '', 23420),
(38729, 'ruRU', 'Возвращение в Черный храм', 'Воспользуйтесь саргеритовым ключом, чтобы активировать портал Скверны и вернуться в Черный храм.', 'Скорее, $n, воспользуйся саргеритовым ключом.\n\nМы должны вернуться в Черный храм, пока еще не поздно!', '', '', '', '', '', '', 23420),
(38728, 'ruRU', 'Ключ', 'Убейте королеву стаи Тиранну и завладейте саргеритовым ключом. Затем присоединитесь к остальным в зале портала.', '$n, королева стаи Тиранна впереди, в главном зале. Саргеритовый ключ – у нее. Остальные уже вступили в бой.\n\nНаконец-то нам достанется то, ради чего мы принесли столько жертв.', '', 'Вырвите саргеритовый ключ из ее мертвых рук!', 'Королева стаи Тиранна', '', '', '', 23420),
(39663, 'ruRU', 'На крыльях сквернотопыря', 'Оседлайте сквернотопыря иллидари Белой Луны и отправляйтесь на "Молот Скверны".', 'Час настал! Мы нанесли войскам Легиона сокрушительный удар, от которого они не смогут оправиться.\n\nБыстрее поднимаемся на флагманский корабль! Нас ждет бой с Тиранной.', '', '', '', '', '', '', 23420),
(39515, 'ruRU', 'Я свершу месть!', 'Обучите своих соратников – охотников на демонов – специализации "Месть".', 'Вот поэтому демоны Легиона нас и боятся. Мы умеем обращать их тайны против них самих и при этом становимся сильнее.\n\nБыстрее, $n, научи нас тому, что тебе удалось узнать.', '', '', '', '', '', '', 23420),
(40051, 'ruRU', 'Тайны Скверны', 'Прочтите фолиант тайн Скверны и выберите одну из специализаций: "Истребление" или "Месть".', 'Демоны допустили огромную ошибку, записав свои секреты в эту книгу. Она излучает могущественную энергию Скверны.\n\nОднако энергии фолианта хватит лишь на один ритуал. После этого книга исчезнет.\n\nВыбери, каким тайным способностям тебе бы хотелось научиться для войны с Пылающим Легионом.', '', '', '', '', '', '', 23420),
(40222, 'ruRU', 'Фолиант матери бесов', 'Достаньте фолиант тайн Скверны.', 'Видишь эту огромную пещеру? В ней укрылась чрезвычайно могущественная мать бесов.\n\nНо это еще не все. У нее есть кое-что, что нам нужно.\n\nПо-видимому, эта мать бесов является хранительницей тайных знаний Скверны. Она исполняет нечестивые ритуалы Легиона и записывает их в свою книгу.\n\nНам нужно заполучить этот фолиант и выведать его секреты!', '', 'Украдите у нее фолиант тайн Скверны, чтобы сделать себя и других иллидари еще сильнее.', 'Плодовия', '', '', '', 23420),
(38819, 'ruRU', 'Число им — легион', 'Нападите на придворных королевы стаи и силы Пылающего Легиона в районе Адского пика.', 'Конечно, разобраться с уничтожителями – наша главная задача, но впереди еще много много трудностей.\n\nПротивник превосходит нас числом, но мы надеемся на твою помощь. Придворные королевы стаи командуют силами Легиона на вулкане. Их следует уничтожить. Мы должны сокрушить войска Легиона и вырвать победу любой ценой.', '', '', '', '', '', '', 23420),
(38725, 'ruRU', 'Нечистый приют', 'Встретьтесь с Кор''вас Кровавый Шип в Нечистом приюте.', 'Один из демонов, которого я допрашивала, поделился интересной информацией. По всей видимости, какая-то мать бесов в вулкане переписывает тайны Легиона.\n\nКор''вас отправилась туда, чтобы разыскать демона и доставить нам секретные данные.', '', '', '', '', '', '', 23420),
(38727, 'ruRU', 'Остановить бомбардировку', 'Разрушьте уничтожители Легиона.', 'Защитные приспособления Джейса долго не продержатся. Кто-то должен отправиться на поле битвы и разрушить все уничтожители Легиона.\n\n<Кайн многозначительно смотрит на вас призрачным зрением.>', '', '', '', '', '', '', 23420),
(43618, 'ruRU', 'РАЗЫСКИВАЕТСЯ: Гурбог Крушака', '', '', '', '', '', '', '', '', 23420),
(39495, 'ruRU', 'Тайное становится явным', 'Убейте повелителя Скверны Кхазу и заберите у него силу Скверны.', 'Теперь, когда ты $gобнаружил:обнаружила; демонов, я тоже их чувствую. Среди них где-то в глубине пещеры находится могущественный повелитель Скверны. Пока есть возможность, попробуй заполучить его силу.\n\nЕсли ты займешься повелителем, мы сможем взять на себя остальных демонов в пещере.', '', 'Заберите его силу и обезопасьте свои войска на Опорном пункте иллидари от нападения с тыла.', 'Повелитель Скверны Кхаза', '', '', '', 23420),
(39262, 'ruRU', 'Лишняя пара глаз', 'Поговорите с Джейсом Темным Ткачом и используйте способность "Призрачное зрение", чтобы обнаружить скрывающихся в пещере демонов.', 'Джейс Темный Ткач уверяет, что на какое-то мгновение почувствовал присутствие демонов где-то над нами. Он захватил с собой отряд бойцов, чтобы проверить пещеру к юго-востоку отсюда.\n\nОни ничего в ней не нашли, но Джейсу кажется, что там все-таки что-то не так.\n\nМожет, ты ему поможешь? А еще лучше просвети там все своим призрачным зрением.', '', '', '', '', '', '', 23420),
(38813, 'ruRU', 'Приказы командирам', 'Сообщите командирам отрядов иллидари план нападения на войска Пылающего Легиона в жерле вулкана.', 'Теперь осталось только отдать приказы командирам отрядов иллидари. Основные силы Легиона расположились в жерле вулкана. Они готовятся перейти в наступление.\n\nНам нужно первыми нанести удар в самое сердце врага.\n\nКогда мы уничтожим основные силы, можно будет подняться на флагманский корабль, чтобы разобраться с королевой стаи Тиранной и забрать у нее саргеритовый ключ. Владыка Иллидан будет нами доволен.', '', '', '', '', '', '', 23420),
(38766, 'ruRU', 'Пока нас не вытеснили', 'Убейте командира стражей ужаса Белиаша и заберите его демоническую силу.', 'Нам нужно разобраться с командиром стражей ужаса и его королевой. Подчиненные ему эредары призывают все новые и новые войска.\n\nМы должны уничтожить его до того, как силы Легиона вытеснят нас с вулкана!\n\nКроме того, ты сможешь забрать себе его силу.', '', 'Это военачальник сил Легиона, занимающих позиции вблизи вулкана. Вам нужно получить его демоническую силу.', 'Командир стражей ужаса Белиаш', '', '', '', 23420),
(38765, 'ruRU', 'Сбор иллидари: шиварры', 'Принесите в жертву могущественную душу, чтобы привести в действие активатор врат Легиона и призвать отряд шиваррских демонесс.', 'Шиваррские демонессы ожидают в Черном храме. Призывать их сюда – большой риск... ведь, в конце концов, раньше они служили Пылающему Легиону. Но они одни из самых сильных союзников владыки Иллидана.\n\n<Джейс изучает вас долгим взглядом.>\n\nУ меня есть ощущение, что для того, чтобы призвать их, понадобится необычная жертва.', '', '', '', '', '', '', 23420),
(39050, 'ruRU', 'Знакомство с королевой', 'Завершите ритуал, чтобы подсмотреть за предводителями сил Легиона.', 'Мы можем использовать часть эссенции, чтобы завершить ритуал. Ты сможешь подсмотреть за предводителями сил Легиона и узнать, что они задумали.\n\nИспользуй горнило Пустоты и заверши ритуал.', '', '', '', '', '', '', 23420),
(38672, 'ruRU', 'Свобода!', 'Освободите Алтруиса Страдальца и Кайна Ярость Солнца.', 'В одиночку нам не выбраться из Казематов.\n\nДвое твоих союзников, Кайн Ярость Солнца и Алтруис Страдалец, заточены в этой камере.\n\nОсвободи их как можно скорее. Их помощь нам очень пригодится.', '', '', '', '', '', '', 23420),
(44137, 'ruRU', 'В гущу боя', 'Поговорите с командиром охотников на демонов на Аванпосте иллидари.', 'Пылающий Легион опередил нас, но и иллидари прибыли раньше. Пока что охотники на демонов вроде бы держатся, но их положение очень шатко.\n\nДумаю, самое время обратить мощь твоего оружия против нашего общего врага. А пока ты будешь крушить прислужников Легиона, я прикину, каким будет наш следующий шаг.', '', '', '', '', '', '', 23420),
(34398, 'ruRU', 'Темный портал', 'Поговорите с верховным магом Кадгаром у подножия Темного портала в Выжженных землях.', '$B|cFF660000 ВНИМАНИЕ, ГЕРОИ,$b ИСКАТЕЛИ ПРИКЛЮЧЕНИЙ,$b ЗАЩИТНИКИ АЗЕРОТА!|r$b$bВерховный маг Кадгар, герой Второй войны, созывает коалицию сильнейших воинов Азерота, готовых пройти через Темный портал и остановить вторжение Железной Орды в Азерот.$b$bЗащитников, жаждущих славы, ждут у основания Темного портала в Выжженных землях.$b$bБезопасность не гарантируется.', '', '', '', '', '', '', 23420),
(33359, 'ruRU', 'Ищи нас на Заставе Падающих Звезд', 'Поговорите с верховным магом Кадгаром на Заставе Падающих Звезд.', 'Верховный маг заподозрил, что в долине есть кто-то из Совета Теней. Он попросил меня сообщить тебе об этом. $B$BРазыщи нас на Заставе Падающих Звезд – и как можно скорее. Врага нужно искоренить, но без твоей помощи, командир, мы не справимся.', '', '', '', '', '', '', 23420),
(34582, 'ruRU', 'Закрепление на Дреноре', 'Следуйте за пророком Веленом.', 'Значит, это ты $gпривел:привела; моих детей домой? Железная Орда становится все сильнее с каждым днем.$b$bЯ предвижу великую битву, но мои видения затуманены. На ткани времени будто образовались складки.$b$bНо одно я могу сказать наверняка: ты сыграешь в грядущих событиях важную роль.', '', '', '', '', '', '', 23420),
(40379, 'ruRU', 'Сбор иллидари: клан Змеиных Колец', 'Принесите в жертву душу, чтобы привести в действие активатор врат Легиона, а затем используйте его, чтобы призвать отряд наг из клана Змеиных Колец.', 'Следующие врата расположены на Раскаленном берегу. Наги из клана Змеиных Колец сейчас находятся в Черном храме и ждут, когда откроется портал.\n\nЛично я не слишком доверяю нагам, но их повелительница поклялась в верности владыке Иллидану, так что посмотрим, что из этого выйдет.\n\nМы должны активировать эти врата. Для них тоже нужны души.\n\n<Аллари Пожирательница Душ хищно улыбается, услышав слова Севиса.>', '', '', '', '', '', '', 23420),
(39049, 'ruRU', 'Всевидящее око', 'Убейте инквизитора Зловеша и заберите его силу.', 'Этот демон бесполезен. Я пыталась разговорить его с помощью Косы Душ, но он сопротивляется. Посмотрим, сколько он продержится.\n\nХотя у меня есть один план...\n\nНеподалеку отсюда расположился еще один демон-инквизитор. Такие демоны используют ужасающие темные ритуалы и видят все с помощью своих летающих глаз. Если бы тебе удалось забрать его силу, Джейс Темный Ткач смог бы пошпионить за Легионом.\n\nВытяни силу из еще теплого трупа демона, и ты сможешь использовать ее в своих целях.', '', 'Заберите его сущность и станьте еще могущественнее.', 'Инквизитор Зловеш', '', '', '', 23420),
(38759, 'ruRU', 'Освободи их', 'Несколько охотников на демонов попали в беду. Раздобудьте кованый ключ душ, чтобы освободить их.', 'Циана отправилась на Раскаленный берег, чтобы помочь охотникам на демонов, которые прошли через врата раньше нас. Но мне кажется, что она попала в беду.\n\nЭтот инквизитор рассказал, что их держат взаперти какие-то необычные демоны, которых называют "тюремщиками". Они высасывают души своих жертв. Не думаю, что наши соратники смогут долго продержаться без помощи.', '', '', '', '', '', '', 23420),
(42275, 'ruRU', 'Я на страже!', '', '', '', '', '', '', '', '', 23420),
(40378, 'ruRU', 'Сбор иллидари: Пеплоусты', 'Призовите отряд Пеплоустов, активировав врата Легиона.', 'Владыка Иллидан назначил тебя командовать операцией. Теперь нам нужно собрать оставшиеся отряды иллидари.\n\nВпереди находятся три пары врат Пылающего Легиона. Активируй их, чтобы призвать войска иллидари, которые ждут сигнала в Черном храме.\n\nКак и для большинства сооружений Легиона, для активации врат нужны души. Думаю, нам уже хватит душ, чтобы открыть первые врата. Давай же призовем отряд Пеплоустов.', '', '', '', '', '', '', 23420),
(39279, 'ruRU', 'Атака на Мардум', '', '', '', '', '', '', '', '', 23420),
(43610, 'ruRU', 'РАЗЫСКИВАЕТСЯ: повелитель инферналов', '', '', '', '', '', '', '', '', 23420),
(41896, 'ruRU', 'Операция "Свободу мурлокам"', '', '', '', '', '', '', '', '', 23420),
(40077, 'ruRU', 'Атака начинается', 'Убейте $1oa |4демона:демонов:демонов; и замените знамя Легиона своим, чтобы занять Хребет отчаяния.', 'Наш передовой отряд охотников на демонов уже прибыл и вступил в бой с Легионом.\n\nПусть демоны узнают о нас! Заставим их нас бояться! Установи наше знамя, $n.', '', '', '', '', '', '', 23420);
