INSERT INTO `gossip_menu_option_locale` (`MenuID`, `OptionID`, `Locale`, `OptionText`, `BoxText`) VALUES
(18424, 0, 'ruRU', 'Почему ты не в ладах с Алтруисом?', 0),
(19012, 0, 'ruRU', 'Сделать выбор между Кайном и Алтруисом', 0),
(18426, 0, 'ruRU', 'Почему тебе не нравится Кайн?', 0),
(18776, 0, 'ruRU', 'Изаль, я возьму одного из твоих сквернотопырей. Мы наконец отправляемся за тем, за чем сюда пришли.', 0),
(18435, 0, 'ruRU', 'Кайн, расскажи мне, что ты знаешь о Мардуме.', 0),
(18823, 1, 'ruRU', 'Маннетрель, готовься. Я собираюсь наделить тебя тайными силами Легиона.', 0),
(18435, 2, 'ruRU', 'Кайн, я открою тебе секреты демонов, которые стали мне известны.', 0),
(18935, 1, 'ruRU', 'Аллари, слушай внимательно, что я узнал.', 0),
(18936, 1, 'ruRU', 'Слушай внимательно, Циана. Вот что я узнал из фолианта тайн Скверны.', 0),
(18937, 1, 'ruRU', 'Ты готова постичь тайны Легиона, Кор''вас?', 0),
(19175, 0, 'ruRU', 'Хорошо, Джейс, я осмотрю пещеру с помощью "Призрачного зрения".', 0),
(17260, 0, 'ruRU', 'Военачальник, ваши войска будут атаковать Проклятую крепость с юго-востока. Так вы отвлечете на себя основную массу демонов.', 0),
(18434, 0, 'ruRU', 'Верховная мать, я хочу, чтобы твои шиварры атаковали Кузню Порчи на другой стороне пропасти. Несите боль и страдания!', 0),
(18447, 0, 'ruRU', 'Миледи, вы с нагами отправитесь на север. Вы должны прервать ритуал, который выполняют демоны у реактора душ.', 0),
(18994, 0, 'ruRU', 'Что-то еще?', 0),
(18720, 0, 'ruRU', 'Спасибо, Песня Ночи. Мне как раз нужно кое-что починить.', 0),
(19133, 1, 'ruRU', 'Погоди немного, Севис. Мне нужно все взвесить.', 0),
(19133, 0, 'ruRU', 'Я решил, Севис. Мне нужно, чтобы ты принес себя в жертву и активировал врата.', 0),
(19016, 1, 'ruRU', 'Я хочу, чтобы ты принес меня в жертву и активировал врата.', 0),
(19016, 0, 'ruRU', 'Севис, мне нужно, чтобы ты принес себя в жертву и активировал последние врата.', 0),
(18438, 1, 'ruRU', 'Что ты думаешь о шиваррских демонессах?', 0),
(18438, 0, 'ruRU', 'Джейс, что такое это "горнило Пустоты"?', 0),
(19015, 0, 'ruRU', 'Спасибо тебе за твое самопожертвование, мистик.', 0),
